def solution_1(A):
    
    position = 0

    for x in xrange(0,len(A)):
        if A[x] == "(" :
            position = position + 1
        elif A[x] == ")" :
            position = position - 1

    return position

def solution_2(A):
    
    position = 0

    for x in xrange(0,len(A)):
        if A[x] == "(" :
            position = position + 1
        elif A[x] == ")" :
            position = position - 1

        if position == -1:
            return x + 1

    return 0


